    private void bcancelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcancelActionPerformed
        // TODO add your handling code here:
        int n=JOptionPane.showConfirmDialog(null,"Marking is not yet completed \n Do you want to cancel it ? ");
        if(n==0){
            this.setVisible(false);
            System.exit(0);
        }
        
    }//GEN-LAST:event_bcancelActionPerformed

    private void bfinishActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bfinishActionPerformed
        // TODO add your handling code here:
        
        Programe p=new Programe();
        p.setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_bfinishActionPerformed

